import pandas as pd
import numpy as np
from collections import Counter
 
def uniqueCharacters(s):
   
    # If at any character more than once create another stream
    # stream count more than 0, return false
    return not any(filter(lambda x: x > 1, list(Counter(list(s)).values())))

bigstr = ''

# bigstr = "mjqjpqmgbljsphdztnvjfqwrcgsmlb"

for i in range(0, len(bigstr)):
   print(bigstr[i:i+4] + ' ' + str(i) )
   if uniqueCharacters(bigstr[i:i+4]):
        print(bigstr[i:i+4] + 'HERE' + str(i+4))
        break

